    <meta name="viewport" content="width=device-width, initial-scale=1.0">

    <title>theplanetstylista</title>

    <!-- font -->

    <link rel="preconnect" href="https://fonts.googleapis.com">

    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>

    <link href="https://fonts.googleapis.com/css2?family=Lora:ital,wght@0,400;0,500;0,600;0,700;1,400;1,500;1,600;1,700&family=Playfair+Display:ital,wght@0,400;0,500;0,600;0,700;0,800;0,900;1,400;1,500;1,600;1,700;1,800;1,900&display=swap" rel="stylesheet">

    <!-- bootstrap-5.0.2 -->

    <link rel="stylesheet" href="./css/bootstrap.min.css">

    <!-- font awesome -->

    <link rel="stylesheet" href="./css/fontawesome-free-6.1.1-web/css/all.min.css">

    <!-- owl corousel  -->

    <link rel="stylesheet" href="./css/owl.carousel.min.css">

    <!-- owl corousel theme -->

    <link rel="stylesheet" href="./css/owl.theme.green.min.css">

    <!-- custome style link -->

    <link rel="stylesheet" href="./css/styles.css">

    <style>

        body {

            color: var(--text-color);

            font-family: 'Playfair Display', serif;

        }

        h1, h2, h3, h4, h6, .h1, .h2, .h3, .h4, .h6 {

            font-family: 'Lora', serif;

        }

        h2, h2 i {

            color: silver !important;

        }

        h2 i {

            color: var(--text-color) !important;

        }

        #registerFormContainer, #registerFormContainer h2 {

            color: #000;

        }

        .text-main {

            color: var(--text-color) !important;

        }

        .border-main {

            border-color: var(--text-color) !important;

        }

        button {

            color: var(--text-alternate) !important;

        }



        /* keyBoardMember */

        #keyBoardMember img {

            transition: transform 1s;

        }

        #keyBoardMember img:hover {

            transform: scale(1.2);

        }

        /* //keyBoardMember */



        /* celebrity */

        #celebrity img {

            transition: transform 1s;

        }

        #celebrity img:hover {

            transform: scale(1.2);

        }

        /* //celebrity */



        /* testimonial carousel */

        #testimonialCarousel .owl-item {

            opacity: 0.5;

            padding: 10px;

            border: 1px solid var(--text-color);

        }

        #testimonialCarousel .owl-item:not(#testimonialCarousel .owl-item.active.center) {

            transform: scale(0.8);

        }

        #testimonialCarousel .owl-item.active.center {

            opacity: 1;

        }

        #testimonialCarousel .owl-nav .owl-prev, #testimonialCarousel .owl-nav .owl-next {

            font-size: 3rem;

            position: absolute;

            top: 50%;

